browser.runtime.onMessage.addListener(function(payload, sender){
    if(payload['recipient'] == 'background'){
        console.log('recieved command ', payload);
        if(payload['func'] == 'sendText'){
            console.log('send color words');
            sendColorWords(payload, sender);
        } else if(payload['func'] == 'storeWord') {
            console.log('storing off word');
            storeWord(payload);
        }
    }
});

browser.commands.onCommand.addListener(function(command) {
    if (command == "add-selected") {
            browser.tabs.query({ currentWindow: true, active: true}).then(sendAddSelectedToTab).catch(onError);
            console.log("add-selected word event!");
          }
});

function sendAddSelectedToTab(tabsData){
    browser.tabs.sendMessage(tabsData[0]['id'], {"func": "addWord"});
}

function onError(fu){
    console.log(fu);
}

function storeWord(payload){
    console.log("payload is ", payload);
    browser.storage.local.set({ [payload["word"]] : payload['wordData'] });
}

function sendColorWords(payload, sender){
    //console.log('text is ', payload);
    //var lang = payload['lang'];
     var text = payload['text'];
   

    // language detector
    var sample = text.slice(0,1000);
    var lang_det = Franc(sample).slice(0,2);
    console.log("detected language is ", lang_det);
    var offset_tokens = tokenize.words()(text);
    console.log('tokens are', offset_tokens);
    var tokens = offset_tokens.map(x => x['value']);
    var tokens_nonums = tokens.filter(x => (!/[^a-zA-ZÀ-ÿ]/.test(x)));
    console.log("tokens_nonums",tokens_nonums);
    var tokens_noproper = removeProperNouns(tokens_nonums);
    console.log("tokens_noproper",tokens_noproper);
    var clean_tokens = filterStopWords(tokens_noproper.map(x => x.toLowerCase()),lang_det);
    console.log("clean_tokens",clean_tokens);
    var counts = createCounts(clean_tokens);
    console.log("counds",counts);
    var sorted_counts = sortedDict(counts);
    console.log("sorted_counts",sorted_counts);
    var words = atLeast3Occur(sorted_counts);
    console.log("words",words);
    console.log('sender is ' , sender);
    
    // history words too
    var foundHistoryWords = new Object;
    var loweredText = text.toLowerCase();
    var storedItemsPromise = browser.storage.local.get(null);
    storedItemsPromise.then((results) => {
        var historyWords = Object.keys(results);
        console.log('searching for history words ', historyWords);
        for (let historyWord of historyWords) {
            var wordData = results[historyWord];
            // only send history words that can be found in text (avoid long computation on client side)
            if(loweredText.search(historyWord) > -1){
                foundHistoryWords[historyWord] = results[historyWord];
            }
        }
    }, onError).then( () => {
        console.log("history words found in text ", Object.keys(foundHistoryWords));
        browser.tabs.sendMessage(sender['tab']['id'], {"func": "highlight", "toHighlight": words.slice(0,100),"wordRankDict": counts, "foundHistoryWords": foundHistoryWords });
    }, onError);
}

function filterStopWords(words, lang){
    var STOPWORD = {
                            "it": STOPWORDS_IT,
                            "hu": STOPWORDS_HU,
                            //"en": STOPWORDS_AM,
                    };
    var TOP1000 = {
                            "it": TOP1000_IT,
                            "hu": TOP4555_HU,
    };

    if(lang == ''){ lang = 'hu'; }
    console.log("Language is", lang);
    var language = lang.toLowerCase().slice(0,2);
	return words.filter(x => !(STOPWORD[language].includes(x)|STOPWORDS_AM.includes(x)|TOP1000[language].includes(x)|x.length < 3));
}

function createCounts(arr){
	var counts = {};

	for (var i = 0; i < arr.length; i++) {
	  var num = arr[i];
	  counts[num] = counts[num] ? counts[num] + 1 : 1;
	}
	return counts;
}

function removeProperNouns(tokens){
    var fracts = {};

    for (var i = 0; i < tokens.length; i++) {
        var word = tokens[i].toLowerCase();
        var first = tokens[i][0];
        if(first == first.toUpperCase()){
            fracts[word] = fracts[word] ? add1both(fracts[word]) : new Array(1, 1);
        }else {
            fracts[word] = fracts[word] ? add1one(fracts[word]) : new Array(0, 1);
        }
    }

    // return only keys of fracts with fract < 0.9
    tokens_no_props = new Array();
    for (var key in fracts){
        if( fracts[key][0]/fracts[key][1] < 0.9){
            tokens_no_props.push(key);
        }
    }
    return tokens.filter(x => tokens_no_props.includes(x.toLowerCase()));
}

function add1both(array2){
    return [array2[0] + 1, array2[1] +1];
}

function add1one(array2){
    return [array2[0], array2[1] +1];
}

function sortedDict(dict){
	var sortable = [];
	for (var key in dict) {
		sortable.push([key, dict[key]]);
	}
	sortable.sort(function(a, b) {
		return b[1] - a[1];
	});
	return sortable;
}

function atLeast3Occur(sorted_counts){
	return sorted_counts.filter(x => x[1] > 2).map(x => x[0]);
}
